mapper.insert:
INSERT INTO tb_article (
    article_no
    , title
    , content
    , writer
    , regdate
    , viewcnt
) VALUES (
    #{article_no}
    , #{title}
    , #{content}
    , #{writer}
    , #{regDate}
    , #{viewCnt}
)

mapper.update:
UPDATE tb_article
SET
    title = #{title}
    , content = #{content}
WHERE
    article_no = #{article_no}
